package basicprograms;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=5;i++) {
			System.out.println(10*i);
		}
		long l1=9876543219l;
		int i1=(int)l1;
		System.out.println("long to int: "+i1);
		float f=121.321f;
		long l=(long)f;
		System.out.println("float to long: "+l);
		int i2=100;
		char inttochar=(char)i2;
		System.out.println("int to char: "+inttochar);
		char ch='a';
		int chtoin=ch;
		System.out.println("char to int: "+chtoin);
	}
}